-- makes a shallow copy of a table, useful for copying job constraints.
function copy(t)
   n = {}
   for k,v in pairs(t) do
      n[k] = v
   end
   return n
end


function Actor:new(o)
   o = o or {
      x = 1, -- tile coordinates
      y = 1,
