function TIC()
   button_state = {
      UP = btn(0),
      UPP = btnp(0),
      DOWN = btn(1),
      DOWNP = btnp(1),
      LEFT = btn(2),
      LEFTP = btnp(2),
      RIGHT = btn(3),
      RIGHTP = btnp(3),
      A = btn(4),
      AP = btnp(4),
      B = btn(5),
      BP = btnp(5)
   }

   cls(0)
   mode:update(button_state, t)
   mode:draw()
   t=t+1

   if mode:done() then
      mode = mode:next()
   end
end




